var gerar=document.getElementById("gerar"),fake_load=document.getElementById("fake_load"),result=document.getElementById("result");const names=fetch("https://raw.githubusercontent.com/Mini-Kraken/api-random_name/main/api.json").then((e=>e.json())).then((e=>e)),resultado=()=>{names.then((e=>{const t=colectConfig();var n=e;n.t=n.m.concat(n.f);const o=[];for(let e=0;e<5;e++){const e=n[t][Math.floor(Math.random()*n[t].length)],a=n.t[Math.floor(Math.random()*n.t.length)];o.push(`${e} ${a}`)}showResult(o)}))};function showResult(e){result.classList.remove("d-none"),result.innerHTML=e.join("<br>")}function restart(){result.classList.add("d-none"),fake_load.classList.add("d-none")}function colectConfig(){var e=document.getElementById("customRadio1"),t=document.getElementById("customRadio2"),n=document.getElementById("customRadio3");return e.checked?"t":t.checked?"m":n.checked?"f":"t"}function start(){restart(),fake_load.classList.remove("d-none"),setTimeout((()=>{resultado(),fake_load.classList.add("d-none"),resultado()}),500)}gerar.addEventListener("click",start);